// Fix JSON prefix issue if present
function fixJsonPrefix(input) {
  if (input.startsWith('json{')) {
    return input.substring(4);
  }
  return input;
}

try {
  // First remove json{ prefix if it exists
  const fixedOutput = fixJsonPrefix(rawOutput);

  // Then parse the JSON
  const parsed = JSON.parse(fixedOutput);

  // Extract the HTML content
  const emailBody = parsed.output;

  return [{ emailBody }];
} catch(e) {
  // Fallback to original behavior if parsing fails
  console.error("Error processing email:", e);
  const emailBody = rawOutput.split('\n').filter(line => !line.startsWith('Subject:')).join('\n');
  return [{ emailBody }];
}

