const mensajes = items
  .slice() // copiamos el array para no mutar el original
  .reverse() // invertimos el orden
  .map(item => {
    return {
      type: item.json.message.type,
      content: item.json.message.content
    };
  });

return [
  {
    json: {
      mensajes
    }
  }
];

const output = $('Basic LLM Chain').first().json.output;

// Aseguramos que fotos_mensaje1 sea un array válido
const fotos1 = Array.isArray(output.fotos_mensaje1) ? output.fotos_mensaje1 : [];

// Si no hay fotos, devolvemos un aviso
if (fotos1.length === 0) {
  return [
    {
      json: {
        mensaje: "No se encontraron fotos en mensaje1"
      }
    }
  ];
}

// Devolvemos un ítem por cada foto
return fotos1.map(link => ({
  json: { Link: link }
}));

