const day = madridTime.getDay(); // 0 = Domingo, 1 = Lunes, ..., 6 = Sábado
const hour = madridTime.getHours();

const enHorario = day >= 1 && day <= 5 && hour >= 9 && hour < 14;

return [
  {
    json: {
      enHorarioComercial: enHorario
    }
  }
];

