return items.map(item => {
  // Extraemos el texto
  const texto = item.json.text;

  // Limpiamos los backticks y la etiqueta ```json
  const jsonLimpio = texto.replace(/```json|```/g, '').trim();

  // Parseamos el string JSON a objeto
  const datos = JSON.parse(jsonLimpio);

  // Devolvemos las claves por separado
  return {
    json: {
      contiene_informacion_sensible: datos.contiene_informacion_sensible,
      idioma: datos.idioma
    }
  };
});
