const item = $input.item;

// Assume input HTML is in item.json.content
const htmlInput = item.json.content;
let sdUrl = null; // Variable to store the found URL

if (typeof htmlInput !== 'string') {
  item.json.error = "Input content is not a string (expected HTML).";
  console.error(item.json.error);
