}


// Regex to find <script type="application/json"> tags and capture their content
const scriptRegex = /<script type="application\/json"[^>]*>([\s\S]*?)<\/script>/g;
let match;

try {
  // Iterate through all matches of the script tag regex
  while ((match = scriptRegex.exec(htmlInput)) !== null) {
    const scriptContent = match[1]; // Group 1 contains the content between the tags

    if (scriptContent) {
      try {
        const jsonData = JSON.parse(scriptContent); // Parse the extracted text as JSON

        // Search within this parsed JSON object for the specific key
        const potentialSdUrl = findValueByKey(jsonData, 'browser_native_sd_url');

        // Check if we found a valid string URL
        if (typeof potentialSdUrl === 'string' && potentialSdUrl.trim() !== '' && potentialSdUrl.includes('.mp4')) {
          sdUrl = potentialSdUrl; // Store the first valid URL found
          console.log("Found potential sd url in script tag:", sdUrl);
          break; // Exit the while loop once found
        }
      } catch (jsonError) {
        // Ignore scripts that don't contain valid JSON or parsing errors
        // console.warn("Skipping script content due to JSON parse error:", jsonError.message);
      }
    }
  } // End while loop

} catch (error) {
   item.json.error = "Error during regex/JSON processing: " + error.message;
   console.error(item.json.error);
   return item; // Return the item with an error field
}


// Add the result (or an error message if not found) to the item's JSON
if (sdUrl) {
  // Clean up potential HTML entities like &
  item.json.extracted_sd_url = sdUrl.replace(/&/g, '&');
  console.log("Successfully extracted and cleaned URL:", item.json.extracted_sd_url);
