// Nodo de código en N8N para combinar múltiples campos en uno solo, separados por saltos de línea

// Este código espera que el objeto de entrada tenga los siguientes campos:
// - Titulo
// - Introduccion
// - Indice
// - Capitulo 1
// - Tema 1 contenido
// - Capitulo 2
// - Tema 2 contenido
// - Capitulo 3
// - Tema 3 contenido
// - Capitulo 4
// - Tema 4 contenido
// - Capitulo 5
// - Tema 5 contenido
// - Fuentes

// Accedemos al primer elemento de entrada (en N8N normalmente hay una lista de items, usamos el primero)
const item = items[0];

// Creamos un nuevo campo llamado "Contenido_combinado" que unirá todos los campos anteriores
// Cada campo se separará con dos saltos de línea (\n\n) para facilitar la lectura
// Si alguno de los campos no existe, se usará una cadena vacía (|| '')
const contenidoCombinado = [
  item.json.Título || '',
  item.json.Introducción || '',
  item.json.Indice || '',
  item.json['Capitulo 1'] || '',
  item.json['Tema 1 contenido'] || '',
  item.json['Capitulo 2'] || '',
  item.json['Tema 2 contenido'] || '',
  item.json['Capitulo 3'] || '',
  item.json['Tema 3 contenido'] || '',
  item.json['Capitulo 4'] || '',
  item.json['Tema 4 contenido'] || '',
  item.json['Capitulo 5'] || '',
  item.json['Tema 5 contenido'] || '',
  item.json.Fuentes || ''
].join('\n\n'); // Unimos todo el contenido con doble salto de línea entre cada bloque

// Añadimos el nuevo campo "Contenido_combinado" al objeto original
item.json.Contenido_combinado = contenidoCombinado;

// Devolvemos el item modificado como resultado del nodo
return [item];

